{
"app_name": "spotify",
"api_name": "search_artists",
"path": "/artists",
"method": "GET",
"description": "Search for artists with a query.",
"parameters": [
{
"name": "query",
"type": "string",
"required": false,
"description": "The search query string.",
"default": "",
"constraints": []
},
{
"name": "genre",
"type": "string",
"required": false,
"description": "The genre to filter the artists by.",
"default": null,
"constraints": []
},
{
"name": "min_follower_count",
"type": "integer",
"required": false,
"description": "The minimum number of followers for search results.",
"default": 0,
"constraints": [
"value >= 0.0"
]
},
{
"name": "max_follower_count",
"type": "integer",
"required": false,
"description": "The maximum number of followers for search results.",
"default": 9223372036854775807,
"constraints": [
"value >= 0.0"
]
},
{
"name": "page_index",
"type": "integer",
"required": false,
"description": "The index of the page to return.",
"default": 0,
"constraints": [
"value >= 0.0"
]
},
{
"name": "page_limit",
"type": "integer",
"required": false,
"description": "The maximum number of results to return per page.",
"default": 5,
"constraints": [
"value >= 1.0, <= 20.0"
]
},
{
"name": "sort_by",
"type": "string",
"required": false,
"description": "The attribute to sort the artists by prefixed with +/- to reflect ascending/descending. Valid attributes: follower_count. If both query and sort_by are given and non-empty, results will be first ranked by query relevance, then paginated, and will then be sorted by the given attribute within each page.",
"default": null,
"constraints": []
}
],
"response_schemas": {
"success": [
{
"artist_id": 1,
"name": "string",
"genre": "string",
"follower_count": 1,
"created_at": "2019-01-01T00:00:00"
}
],
"failure": {
"message": "string"
}
}
}

