{
"app_name": "spotify",
"api_name": "show_liked_songs",
"path": "/liked_songs",
"method": "GET",
"description": "Get a list of songs you have liked.",
"parameters": [
{
"name": "access_token",
"type": "string",
"required": true,
"description": "Access token obtained from spotify app login.",
"default": null,
"constraints": []
},
{
"name": "page_index",
"type": "integer",
"required": false,
"description": "The index of the page to return.",
"default": 0,
"constraints": [
"value >= 0.0"
]
},
{
"name": "page_limit",
"type": "integer",
"required": false,
"description": "The maximum number of results to return per page.",
"default": 5,
"constraints": [
"value >= 1.0, <= 20.0"
]
},
{
"name": "sort_by",
"type": "string",
"required": false,
"description": "The attribute to sort the liked songs by prefixed with +/- to reflect ascending/descending. Valid attributes: liked_at, play_count and title.",
"default": "-liked_at",
"constraints": []
}
],
"response_schemas": {
"success": [
{
"song_id": 1,
"title": "string",
"album_id": 1,
"album_title": "string",
"duration": 1,
"artists": [
{
"id": 1,
"name": "string"
}
],
"liked_at": "2019-01-01T00:00:00"
}
],
"failure": {
"message": "string"
}
}
}

