                nx, ny = x - step * dx, y - step * dy
                if 0 <= nx < self.board_size and 0 <= ny < self.board_size and self.board[ny][nx] == self.current_player:
                    count += 1
                else:
                    break
            if count >= 5:
                return True
        return False
    def reset_game(self):
        self.board = [[None for _ in range(self.board_size)] for _ in range(self.board_size)]
